import styled from 'styled-components';
import { blue, white } from "../constants.styled";

const Banner = styled.div`
    display: flex;
    width: 100%;
    height: 400px;
    background-color: turquoise;
`;

const Button = styled.a`
    padding: 10px 20px;
    border-radius: 2px;
    background-color: ${props => props.primary ? blue : white};
`;

export default {
    Banner,
    Button
}
