import styled from 'styled-components';
import { dark_grey, blue, white } from "../constants.styled";
import { style_mapping } from "../../styled";

const {
    layout: { Grid }
} = style_mapping;

const Banner = styled.div`
    display: flex;
    width: 100%;
    height: 400px;
    background-color: turquoise;
`;

const WidgetContainer = styled.div`
    background-color: #fdfdfd;
    border-radius: 5px;
    border: 1px solid #ede9e9;
    margin-bottom: 18px;

    h2 {
      color: #424242;
      display: inline-block;
      font-size: 15px;
      font-weight: 500;
      margin: 0 0 30px 0;
      padding: 20px 20px 10px;
      position: relative;
      text-transform: capitalize;
      width: 100%;

      :before {
        bottom: 0;
        content: "";
        height: 2px;
        left: 20px;
        position: absolute;
        width: 20%;
        background: ${blue};
      }
    }

    ul {
      display: inline-block;
      list-style: outside none none;
      margin-bottom: 0;
      padding: 0 25px 20px;
      width: 100%;
      margin: 0;
      color: #797979;

      li:not(:last-child) {
        margin-bottom: 20px;
        width: 100%;
      }
    }
`;

const Button = styled.a`
    padding: 10px 20px;
    border-radius: 2px;
    background-color: ${props => props.primary ? blue : white};
    color: ${props => props.primary ? white : dark_grey};
`;

const SidebarContainer = styled.div`
    flex-basis: 250px;
    margin-right: 20px;
`;

const MainContentContainer = styled.div`
    flex: 1;
`;

export default {
    Banner,
    Button,
    WidgetContainer,
    SidebarContainer,
    MainContentContainer
}
